import React, { useState, useEffect } from 'react';

const SmallestNumberGame = () => {
  const [numbers, setNumbers] = useState([]);
  const [result, setResult] = useState('');
  const [questionIndex, setQuestionIndex] = useState(0);
  const [startTime, setStartTime] = useState(null);
  const [endTime, setEndTime] = useState(null);

  useEffect(() => {
    // Generate an array of random numbers
    const generatedNumbers = Array.from({ length: 10 }, () => Math.floor(Math.random() * 100) + 1);
    setNumbers(generatedNumbers);
    setStartTime(new Date());
  }, []);

  useEffect(() => {
    if (questionIndex === 10) {
      // End the game after 10 questions
      setEndTime(new Date());
      calculateScore();
    }
  }, [questionIndex]);

  const checkSmallestNumber = (selectedNumber, selectedIndex) => {
    const isSmallest = numbers.every(
      (number, index) => index === selectedIndex || selectedNumber <= number
    );

    if (isSmallest) {
      setResult(`Correct! ${selectedNumber} is the smallest number.`);
    } else {
      setResult(`Try again! ${selectedNumber} is not the smallest number.`);
    }

    // Move to the next question
    setQuestionIndex(prevIndex => prevIndex + 1);
  };

  const calculateScore = () => {
    const totalTime = (endTime - startTime) / 1000; // in seconds
    const score = Math.round((10 / totalTime) * 100); // simple scoring, adjust as needed
    setResult(`Game Over! Total time: ${totalTime.toFixed(2)} seconds. Your score: ${score}`);
  };

  const restartGame = () => {
    setQuestionIndex(0);
    setResult('');
    setStartTime(new Date());
    setEndTime(null);

    // Generate new random numbers for the next game
    const newNumbers = Array.from({ length: 10 }, () => Math.floor(Math.random() * 100) + 1);
    setNumbers(newNumbers);
  };

  return (
    <div>
      <h1>Select the Smallest Number</h1>
      <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '20px' }}>
        {numbers.map((number, index) => (
          <button key={index} onClick={() => checkSmallestNumber(number, index)}>
            {number}
          </button>
        ))}
      </div>
      <p>{result}</p>
      {questionIndex === 10 && (
        <div>
          <p>Game Over!</p>
          <p>Total time: {(endTime - startTime) / 1000} seconds</p>
          <p>Your score: {Math.round((10 / ((endTime - startTime) / 1000)) * 100)}</p>
          <button onClick={restartGame}>Restart Game</button>
        </div>
      )}
    </div>
  );
};

export default SmallestNumberGame;
