import React, { useState, useEffect } from 'react';
import './Task1.css';

const SmallestNumberGame = () => {
  const [numbers, setNumbers] = useState([]);
  const [result, setResult] = useState('');
  const [questionIndex, setQuestionIndex] = useState(0);
  const [startTime, setStartTime] = useState(null);
  const [endTime, setEndTime] = useState(null);
  const [gameStarted, setGameStarted] = useState(false);

  useEffect(() => {
    if (gameStarted) {
      startNewSet();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [questionIndex, gameStarted]);

  const startNewSet = () => {
    if (questionIndex === 5) {
      // End the game after 5 sets of questions
      setEndTime(new Date());
      calculateScore();
    } else {
      // Generate new random numbers for the next set of questions
      const newNumbers = Array.from({ length: 5 }, () => Math.floor(Math.random() * 100) + 1);
      setNumbers(newNumbers);
      setResult('');
      setStartTime(new Date());
    }
  };

  const checkSmallestNumber = (selectedNumber, selectedIndex) => {
    const isSmallest = numbers.every(
      (number, index) => index === selectedIndex || selectedNumber <= number
    );

    if (isSmallest) {
      setResult('YES! You are right!');
    } else {
      setResult('OOPS! You are wrong.');
    }

    // Move to the next set of questions
    setQuestionIndex(prevIndex => prevIndex + 1);
  };

  const calculateScore = () => {
    // Calculate total time and score after 5 sets of questions
    if (endTime) {
      const totalTime = (endTime - startTime) / 1000; // in seconds
      const score = Math.round((5 / totalTime) * 100); // simple scoring, adjust as needed
      setResult(`Game Over! ${score > 0 ? `Total time: ${totalTime.toFixed(2)} seconds. Your score: ${score}` : ''}`);
    }
  };

  const restartGame = () => {
    setQuestionIndex(0);
    setResult('');
    setStartTime(new Date());
    setEndTime(null);
    setGameStarted(true);
  };

  const goBack = () => {
    // Go back to the previous set of questions
    setQuestionIndex(prevIndex => Math.max(prevIndex - 1, 0));
  };

  return (
    <div>
      {gameStarted && (
        <div>
          <h1>Select the Smallest Number</h1>
          <div style={{ display: 'flex', justifyContent: 'space-around', marginTop: '20px' }}>
            {numbers.map((number, index) => (
              <button key={index} onClick={() => checkSmallestNumber(number, index)}>
                {number}
              </button>
            ))}
          </div>
          <p>{result}</p>
          {questionIndex === 5 && (
            <div>
              <p>Game Over!</p>
              {endTime && (
                <div>
                  <p>Total time: {(endTime - startTime) / 1000} seconds</p>
                  <p>Your score: {Math.round((5 / ((endTime - startTime) / 1000)) * 100)}</p>
                </div>
              )}
              <button onClick={restartGame}>Restart Game</button>
              <button onClick={goBack}>Go Back</button>
            </div>
          )}
        </div>
      )}
      {!gameStarted && (
        <div>
          <button onClick={restartGame}>Start</button>
        </div>
      )}
    </div>
  );
};

export default SmallestNumberGame;
