import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Task1.css';

const SmallestNumberGame = () => {
  const [numbers, setNumbers] = useState([]);
  const [result, setResult] = useState('');
  const [questionIndex, setQuestionIndex] = useState(0);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [questionsCompleted, setQuestionsCompleted] = useState(0);
  const [startTime, setStartTime] = useState(null);
  const [endTime, setEndTime] = useState(null);
  const [gameStarted, setGameStarted] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const navigate = useNavigate();
  const resultTimeout = useRef(null);

  const sendCompletedQuestions = async (completed) => {
    try {
      const token = window.localStorage.getItem('token');

      const response = await fetch('http://localhost:5000/getCompletedQuestions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          task: 'Task1',
          completed: completed,
        }),
      });

      const data = await response.json();
      console.log(data);

      // Navigate or handle the response as needed
    } catch (error) {
      console.error('Error saving completed questions:', error);
    }
  };

  useEffect(() => {
    if (questionsCompleted > 0) {
      sendCompletedQuestions(questionsCompleted);
    }
  }, [questionsCompleted]);

  const calculateScore = () => {
    if (endTime) {
      const accuracy = (correctAnswers / 5) * 100;
      setResult(`Game Over! Your score: ${correctAnswers}/5\nAccuracy: ${accuracy.toFixed(0)}%`);
      setShowResult(true); // Ensure that the result is displayed
    }
  };

  const startNewSet = useCallback(() => {
    if (questionIndex === 5) {
      setEndTime(new Date());
      calculateScore();
    } else {
      const newNumbers = Array.from({ length: 5 }, () => Math.floor(Math.random() * 100) + 1);
      setNumbers(newNumbers);
      setResult('');
      setShowResult(false);
      setStartTime(new Date());
    }
  }, [questionIndex]);

  useEffect(() => {
    startNewSet(); // Initial set of numbers when the component mounts
  }, []); 

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = (seconds % 60).toFixed(2);
    return `${minutes} min ${remainingSeconds} seconds`;
  };

  const checkSmallestNumber = (selectedNumber, selectedIndex) => {
    const isSmallest = numbers.every(
      (number, index) => index === selectedIndex || selectedNumber <= number
    );

    if (isSmallest) {
      setResult('YES! You are right!');
      setCorrectAnswers((prevCorrectAnswers) => prevCorrectAnswers + 1);
    } else {
      setResult('OOPS! You are wrong.');
    }

    setShowResult(true);

    clearTimeout(resultTimeout.current);

    resultTimeout.current = setTimeout(() => {
      setShowResult(false);
      setQuestionIndex((prevQuestionIndex) => prevQuestionIndex + 1);
      startNewSet(); // Generate a new set of numbers for the next question
    }, 1000);
  };

  const restartGame = () => {
    setQuestionIndex(0);
    setResult('');
    setCorrectAnswers(0);
    setStartTime(new Date());
    setEndTime(null);
    setGameStarted(true);
    setShowResult(false);
    startNewSet();
  };

  const goBack = async () => {
    try {
      const token = window.localStorage.getItem('token');

      const response = await fetch('http://localhost:5000/saveCompletedQuestions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          task: 'Task1',
          completed: questionsCompleted,
        }),
      });

      const data = await response.json();
      console.log(data);

      navigate('/dashboard', { state: { questionsCompleted } });
    } catch (error) {
      console.error('Error saving completed questions:', error);
    }
  };

  return (
    <div className="game-container">
      {!gameStarted && (
        <div className="game-description">
          <h1>Select the Smallest Number</h1>
          <p>
            Test your skills by identifying the smallest number in each set of numbers. Click on
            the number you believe is the smallest. Can you get them all right?
          </p>
        </div>
      )}

      {gameStarted && questionIndex <= 4 && (
        <div>
          <p>Select the smallest number!</p>
          <div className="number-buttons">
            {numbers.map((number, index) => (
              <button key={index} onClick={() => checkSmallestNumber(number, index)}>
                {number}
              </button>
            ))}
          </div>
          <p>{showResult && result}</p>
        </div>
      )}

      {questionIndex === 5 && (
        <div>
          <p>Game Over!</p>
          {endTime && (
            <div>
              <p>Total time: {formatTime((endTime - startTime) / 1000)}</p>
              <p>Your score: {correctAnswers}/5</p>
              <p>Accuracy: {(correctAnswers / 5) * 100}%</p>
            </div>
          )}
          <button onClick={restartGame}>Restart Game</button>
          <button onClick={goBack}>Go Back</button>
        </div>
      )}

      {!gameStarted && (
        <div className="start-button-container">
          <button className="start-button" onClick={restartGame}>
            Start Game
          </button>
        </div>
      )}
    </div>
  );
};

export default SmallestNumberGame;
