// MatchTheColumnGame.js

import React, { useState, useEffect } from 'react';
import './Task2.css';

const MatchTheColumnGame = () => {
  const [columnA, setColumnA] = useState(['Apple', 'Banana', 'Cherry', 'Grapes']);
  const [columnB, setColumnB] = useState(['Red', 'Yellow', 'Red', 'Purple']);
  const [selectedCellA, setSelectedCellA] = useState(null);
  const [selectedCellB, setSelectedCellB] = useState(null);
  const [correctMatches, setCorrectMatches] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  const checkMatch = () => {
    if (columnB[selectedCellA] === columnB[selectedCellB]) {
      setCorrectMatches((prevMatches) => prevMatches + 1);
      if (correctMatches === columnA.length - 1) {
        setGameOver(true);
      }
    }

    // Reset selected cells after checking
    setSelectedCellA(null);
    setSelectedCellB(null);
  };

  useEffect(() => {
    if (selectedCellA !== null && selectedCellB !== null) {
      checkMatch();
    }
  }, [selectedCellA, selectedCellB, correctMatches, columnA.length]);

  const restartGame = () => {
    setColumnA(['Apple', 'Banana', 'Cherry', 'Grapes']);
    setColumnB(['Red', 'Yellow', 'Red', 'Purple']);
    setSelectedCellA(null);
    setSelectedCellB(null);
    setCorrectMatches(0);
    setGameOver(false);
  };

  return (
    <div>
      {gameOver ? (
        <div>
          <p>Congratulations! You matched all the pairs.</p>
          <button onClick={restartGame}>Play Again</button>
        </div>
      ) : (
        <div>
          <h2>Match the Columns</h2>
          <div className="grid">
            <div className="column">
              {columnA.map((value, index) => (
                <div
                  key={index}
                  className={`cell ${selectedCellA === index ? 'selected' : ''}`}
                  onClick={() => setSelectedCellA(index)}
                >
                  {value}
                </div>
              ))}
            </div>
            <div className="column">
              {columnB.map((value, index) => (
                <div
                  key={index}
                  className={`cell ${selectedCellB === index ? 'selected' : ''}`}
                  onClick={() => setSelectedCellB(index)}
                >
                  {value}
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MatchTheColumnGame;
