// Task3.js

import React, { useState, useEffect } from 'react';
import './Task3.css';

const FillInBlanksGame = () => {
  const [number1, setNumber1] = useState(0);
  const [number2, setNumber2] = useState(0);
  const [userAnswer, setUserAnswer] = useState('');
  const [resultMessage, setResultMessage] = useState('');
  const [questionIndex, setQuestionIndex] = useState(1);
  const [correctAnswers, setCorrectAnswers] = useState(0);

  useEffect(() => {
    if (questionIndex <= 5) {
      generateRandomNumbers();
    } else {
      displayScore();
    }
  }, [questionIndex]);

  const generateRandomNumbers = () => {
    setNumber1(Math.floor(Math.random() * 10) + 1);
    setNumber2(Math.floor(Math.random() * 10) + 1);
    setUserAnswer('');
    setResultMessage('');
  };

  const handleInputChange = (event) => {
    // Allow only numbers in the input field
    const enteredValue = event.target.value.replace(/[^0-9]/g, '');
    setUserAnswer(enteredValue);
  };

  const handleCheckAnswer = () => {
    const answer = parseInt(userAnswer, 10);
    const correctAnswer = number1 + number2;

    if (!isNaN(answer)) {
      if (answer === correctAnswer) {
        setResultMessage(`Correct! ${number1} + ${number2} = ${correctAnswer}`);
        setCorrectAnswers((prevCorrectAnswers) => prevCorrectAnswers + 1);
      } else {
        setResultMessage('Oops! Try again.');
      }
    } else {
      setResultMessage('Please enter a valid number.');
    }
  };

  const displayScore = () => {
    setResultMessage(`Your score: ${correctAnswers}/5`);
  };

  const handleNextQuestion = () => {
    setQuestionIndex((prevIndex) => prevIndex + 1);
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      if (resultMessage === '') {
        // If result message is empty, check the answer
        handleCheckAnswer();
      } else {
        // If result message is not empty, move to the next question
        handleNextQuestion();
      }
    }
  };

  return (
    <div className="fill-in-blanks-container">
      <div className="question-container">
        {questionIndex <= 5 ? (
          <>
            <p>Question {questionIndex}:</p>
            <p>W{number1} + {number2} = ?</p>
            <input
              type="text"
              value={userAnswer}
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              placeholder="Your answer"
            />
            <button onClick={handleCheckAnswer}>Check Answer</button>
          </>
        ) : (
          <p>{resultMessage}</p>
        )}
        {resultMessage && questionIndex <= 5 && (
          <button className="next-question" onClick={handleNextQuestion}>
            Next Question
          </button>
        )}
      </div>
    </div>
  );
};

export default FillInBlanksGame;
