import React, { useState } from 'react';
import './Task3.css';

const FillInBlanksGame = () => {
  const [number1, setNumber1] = useState('');
  const [number2, setNumber2] = useState('');
  const [result, setResult] = useState('');

  const handleInputChange = (inputValue, setNumber) => {
    const isNumeric = /^[0-9]+$/.test(inputValue);
    if (isNumeric || inputValue === '') {
      setNumber(inputValue);
    }
  };

  const checkAnswer = () => {
    const answer = parseInt(number1) + parseInt(number2);
    setResult(`Your answer: ${answer}`);
  };

  return (
    <div>
      <h2>Fill in the Blanks</h2>
      <p>What is the sum of:</p>
      <div className="input-container">
        <input
          type="text"
          placeholder="Enter number"
          value={number1}
          onChange={(e) => handleInputChange(e.target.value, setNumber1)}
        />
        +
        <input
          type="text"
          placeholder="Enter number"
          value={number2}
          onChange={(e) => handleInputChange(e.target.value, setNumber2)}
        />
        =
        <input
          type="text"
          placeholder="Your answer"
          readOnly
          value={result}
        />
      </div>
      <button onClick={checkAnswer}>Check Answer</button>
    </div>
  );
};

export default FillInBlanksGame;
