// RightSidebar.jsx
import React, { useEffect, useState } from 'react';
import decodeToken from 'path-to-your/utils/auth'; // Adjust the path accordingly

function RightSidebar() {
  const [userInfo, setUserInfo] = useState(null);
  const [tasksCompleted, setTasksCompleted] = useState(0);

  useEffect(() => {
    // Fetch token from local storage
    const token = localStorage.getItem('token');
    
    if (token) {
      const decoded = decodeToken(token);
      
      if (decoded) {
        setUserInfo(decoded);
        
        // Fetch tasks based on username (You need to implement this endpoint in your backend)
        fetchTasks(decoded.username);
      }
    }
  }, []);

  const fetchTasks = async (username) => {
    try {
      const response = await fetch(`http://localhost:5000/getTasks/${username}`); // Replace with your actual endpoint
      if (response.ok) {
        const tasks = await response.json();

        // Count completed tasks
        const completedTasksCount = tasks.reduce((count, task) => count + (task.completed > 0 ? 1 : 0), 0);

        setTasksCompleted(completedTasksCount);
      } else {
        console.error('Failed to fetch tasks');
      }
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };
  return (
    <aside className="right-section">
      
      <div className="separator" id="first">
        <h4>Statistics</h4>
      </div>

      <div className="stats">
        <div className="item">
          <div className="top">
            <p>Tasks</p>
            <p>Completed</p>
          </div>
          <div className="bottom">
            <div className="line"></div>
            <h3>02</h3>
          </div>
        </div>
        <div className="item">
          <div className="top">
            <p>Total Points</p>
            <p>Gained</p>
          </div>
          <div className="bottom">
            <div className="line"></div>
            <h3>250</h3>
          </div>
        </div>
        <div className="item">
          <div className="top">
            <p>Courses</p>
            <p>In Progress</p>
          </div>
          <div className="bottom">
            <div className="line"></div>
            <h3>03</h3>
          </div>
        </div>
        <div className="item">
          <div className="top">
            <p>Favourite</p>
            <p>Task</p>
          </div>
          <div className="bottom">
            <div className="line"></div>
            <h3>250</h3>
          </div>
        </div>
      </div>

      <div className="separator">
        <h4>Weekly Work</h4>
      </div>

      <div className="weekly">
        <div className="title">
          <div className="line"></div>
          <h5>Week Status</h5>
        </div>
        <div className="progress-bar" role="progressbar" aria-valuenow="75" aria-valuemin="0" aria-valuemax="100">
          <div className="progress"><p>3/4</p></div>
        </div>
      </div>
    </aside>
  );
}

export default RightSidebar;
